(function () {
  "use strict";

  function getTOCInfo(tiddler, includeHeaderMap) {
    // Check empty
    if (tiddler === "") return undefined;
    var currentTiddler = $tw.wiki.getTiddler(tiddler);
    if (!currentTiddler) return undefined;
    var type = currentTiddler.fields.type;
    if (
      type &&
      type !== "" &&
      type !== "text/vnd.tiddlywiki" &&
      type !== "text/x-markdown"
    )
      return undefined;
    var headers = [];
    var headersCount = {
      h1: 0,
      h2: 0,
      h3: 0,
      h4: 0,
      h5: 0,
      h6: 0,
    };
    var root = $tw.wiki.parseTiddler(tiddler).tree;
    if (root.length == 0)
      return undefined;
    var renderRoot = [],
      renderLeaf = renderRoot;
    // Parse params
    while (["set", "importvariables"].indexOf(root[0].type) > -1) {
      renderRoot = [
        Object.assign({}, root[0], {
          children: renderRoot,
        }),
      ];
      root = root[0].children;
    }
    $tw.utils.each(root, function (node) {
      if (node.type !== "element") return;
      if (includeHeaderMap[node.tag] !== true) return;
      // Clear and re-fill
      renderLeaf.splice(0, renderLeaf.length);
      renderLeaf.push.apply(renderLeaf, node.children);
      // Render contents of header
      var container = $tw.fakeDocument.createElement("div");
      $tw.wiki
        .makeWidget(
          {
            tree: renderRoot,
          },
          {}
        )
        .render(container, null);
      headers.push({
        tag: node.tag,
        count: headersCount[node.tag]++,
        text: container.textContent,
      });
    });
    return {
      title: tiddler,
      headers: headers,
    };
  }

  var Widget = require("$:/core/modules/widgets/widget.js").widget;
  var PageTOCWidget = function (parseTreeNode, options) {
    this.initialise(parseTreeNode, options);
  };
  PageTOCWidget.prototype = new Widget();
  PageTOCWidget.prototype.render = function (parent, nextSibling) {
    this.parentDomNode = parent;
    if (
      this.parentWidget &&
      this.parentWidget.hasVariable("page-toc-recursion-detection", "yes")
    ) {
      parent.insertBefore(
        this.document.createTextNode("[Page TOC]"),
        nextSibling
      );
      return;
    }
    this.setVariable("page-toc-recursion-detection", "yes");
    this.computeAttributes();
    this.execute();
    this.makeRender(parent, nextSibling);
  };
  PageTOCWidget.prototype.execute = function () {
    // Get our parameters
    this.tocTitle = this.getAttribute(
      "tiddler",
      this.getVariable("currentTiddler")
    );
    this.tocNodeTag = this.getAttribute("tag", "div");
    if (
      $tw &&
      $tw.config &&
      $tw.config.htmlUnsafeElements &&
      $tw.config.htmlUnsafeElements.indexOf(this.tocNodeTag) !== -1
    )
      this.tocNodeTag = "div";
    this.tocHeaderNodeTag = this.getAttribute("headerTag", "p");
    if (
      $tw &&
      $tw.config &&
      $tw.config.htmlUnsafeElements &&
      $tw.config.htmlUnsafeElements.indexOf(this.tocHeaderNodeTag) !== -1
    )
      this.tocHeaderNodeTag = "p";
    this.tocNodeClass = this.getAttribute(
      "class",
      "gk0wk-tiddlertoc-container"
    );
    this.tocHeaderNodeClassPrefix = this.getAttribute(
      "headerClassPrefix",
      "gk0wk-tiddlertoc-"
    );
    this.emptyMessage = this.getAttribute("emptyMessage", "");
    this.includeHeaderMap = {
      h1: this.getAttribute("h1", "yes") === "yes",
      h2: this.getAttribute("h2", "yes") === "yes",
      h3: this.getAttribute("h3", "yes") === "yes",
      h4: this.getAttribute("h4", "yes") === "yes",
      h5: this.getAttribute("h5", "yes") === "yes",
      h6: this.getAttribute("h6", "yes") === "yes",
    };
    this.scrollMode = this.getAttribute("scrollMode", "center");
    if (["start", "center", "end", "nearest"].indexOf(this.scrollMode) === -1)
      this.scrollMode = "center";
    var info = this.wiki.getTextReferenceParserInfo(
      this.tocTitle,
      "text",
      "",
      {}
    );
    this.sourceText = info.sourceText;
    this.parserType = info.parserType;
  };

  PageTOCWidget.prototype.parserNeedsRefresh = function () {
    var parserInfo = this.wiki.getTextReferenceParserInfo(
      this.tocTitle,
      "text",
      "",
      {}
    );
    return (
      !this.sourceText ||
      parserInfo.sourceText !== this.sourceText ||
      !this.parserType ||
      parserInfo.parserType !== this.parserType
    );
  };
  PageTOCWidget.prototype.refresh = function (changedTiddlers) {
    var changedAttributes = this.computeAttributes();
    if (
      $tw.utils.count(changedAttributes) > 0 ||
      (changedTiddlers[this.tocTitle] && this.parserNeedsRefresh())
    ) {
      this.refreshSelf();
      return true;
    } else {
      return this.refreshChildren(changedTiddlers);
    }
  };

  PageTOCWidget.prototype.makeRender = function (parent, nextSibling) {
    var tocNode = this.document.createElement(this.tocNodeTag);
    tocNode.className = this.tocNodeClass;
    this.domNodes = [tocNode];
    try {
      var toc = getTOCInfo(this.tocTitle, this.includeHeaderMap);
      var headerNode;
      if (!toc || toc.headers.length === 0) {
        headerNode = document.createElement(this.tocHeaderNodeTag);
        headerNode.className = this.tocHeaderNodeClassPrefix + "empty";
        headerNode.innerText = this.emptyMessage;
        tocNode.appendChild(headerNode);
      } else {
        for (var i = 0, len = toc.headers.length; i < len; i++) {
          var header = toc.headers[i];
          headerNode = this.document.createElement(this.tocHeaderNodeTag);
          headerNode.className = this.tocHeaderNodeClassPrefix + header.tag;
          headerNode.innerText = header.text;
          if (headerNode.setAttribute && headerNode.addEventListener) {
            headerNode.setAttribute("index", i.toString());
            var scrollMode = this.scrollMode;
            headerNode.addEventListener("click", function () {
              try {
                var tiddlerFrameNode = document.querySelector(
                  '.tc-tiddler-frame[data-tiddler-title="' +
                    toc.title.replace('"', '\\"') +
                    '"]'
                );
                // var tiddlerFrameNode = headerNode;
                if (!tiddlerFrameNode) return;
                var headerInfo =
                  toc.headers[parseInt(this.getAttribute("index"))];
                if (!headerInfo) return;
                var _headerNode = tiddlerFrameNode.querySelectorAll(
                  ".tc-tiddler-body > " + headerInfo.tag
                )[headerInfo.count];
                if (!_headerNode) return;
                if (scrollMode === "center" || scrollMode === "nearest") {
                  _headerNode.scrollIntoView({
                    behavior: "smooth",
                    block: scrollMode,
                  });
                } else {
                  // Position fix
                  _headerNode.scrollIntoView({
                    behavior: "instant",
                    block: scrollMode,
                  });
                  if (tscrollMode === "end") {
                    document.body.scrollTop += 100;
                    document.scrollingElement.scrollTop += 100;
                  } else {
                    document.body.scrollTop -= 100;
                    document.scrollingElement.scrollTop -= 100;
                  }
                }
              } catch (e) {
                console.error(e);
              }
            });
          }
          tocNode.appendChild(headerNode);
        }
      }
    } catch (e) {
      console.error(e);
      tocNode.innerText = String(e);
    }
    parent.insertBefore(tocNode, nextSibling);
  };
  exports["page-toc"] = PageTOCWidget;
})();

$:/plugins/Gk0Wk/page-toc/style.css.text:
.gk0wk-tiddlertoc-h1 {
  padding-left: 0;
}
.gk0wk-tiddlertoc-h2 {
  padding-left: 1em;
}
.gk0wk-tiddlertoc-h3 {
  padding-left: 2em;
}
.gk0wk-tiddlertoc-h4 {
  padding-left: 3em;
}
.gk0wk-tiddlertoc-h5 {
  padding-left: 4em;
}
.gk0wk-tiddlertoc-h6 {
  padding-left: 5em;
}

.gk0wk-tiddlertoc-empty {
  color: <<colour very-muted-foreground>>;
  user-select: none;
}

.gk0wk-tiddlertoc-h1,
.gk0wk-tiddlertoc-h2,
.gk0wk-tiddlertoc-h3,
.gk0wk-tiddlertoc-h4,
.gk0wk-tiddlertoc-h5,
.gk0wk-tiddlertoc-h6 {
  user-select: none;
  cursor: pointer;
  transition: all {{$:/config/AnimationDuration}}ms;
}

.gk0wk-tiddlertoc-h1:hover,
.gk0wk-tiddlertoc-h2:hover,
.gk0wk-tiddlertoc-h3:hover,
.gk0wk-tiddlertoc-h4:hover,
.gk0wk-tiddlertoc-h5:hover,
.gk0wk-tiddlertoc-h6:hover {
  color: <<colour foreground>>;
}

.gk0wk-tiddlertoc-viewbuttonpopup {
  border-radius: 3px;
  background: <<colour page-background>>;
  width: min(90vw, 300px);
  <<box-shadow "#00000070 0 2px 6px">>
  filter: contrast(90%);
  padding: 0;
  overflow-y: auto;
  max-height: 80vh;
}

.gk0wk-tiddlertoc-viewbuttonpopup-h1 {
  padding-left: 16px;
}

.gk0wk-tiddlertoc-viewbuttonpopup-h2 {
  padding-left: calc(16px + 1em);
}

.gk0wk-tiddlertoc-viewbuttonpopup-h3 {
  padding-left: calc(16px + 2em);
}

.gk0wk-tiddlertoc-viewbuttonpopup-h4 {
  padding-left: calc(16px + 3em);
}

.gk0wk-tiddlertoc-viewbuttonpopup-h5 {
  padding-left: calc(16px + 4em);
}

.gk0wk-tiddlertoc-viewbuttonpopup-h6 {
  padding-left: calc(16px + 5em);
}

.gk0wk-tiddlertoc-viewbuttonpopup-empty {
  color: <<colour very-muted-foreground>>;
  user-select: none;
  font-size: 24px;
  line-height: 1em;
  padding: 20px;
  text-align: center;
  margin: 0 !important;
}

.gk0wk-tiddlertoc-viewbuttonpopup-h1,
.gk0wk-tiddlertoc-viewbuttonpopup-h2,
.gk0wk-tiddlertoc-viewbuttonpopup-h3,
.gk0wk-tiddlertoc-viewbuttonpopup-h4,
.gk0wk-tiddlertoc-viewbuttonpopup-h5,
.gk0wk-tiddlertoc-viewbuttonpopup-h6 {
  font-size: 16px;
  font-weight: 700;
  line-height: 1em;
  margin: 0 !important;
  padding-top: 10px;
  padding-bottom: 10px;
  padding-right: 6px;
  user-select: none;
  cursor: pointer;
  background: transparent;
  opacity: 0.7;
  white-space: nowrap;
  text-overflow: ellipsis;
  overflow: hidden;
  transition: all {{$:/config/AnimationDuration}}ms;
}

.gk0wk-tiddlertoc-viewbuttonpopup-h1:hover,
.gk0wk-tiddlertoc-viewbuttonpopup-h2:hover,
.gk0wk-tiddlertoc-viewbuttonpopup-h3:hover,
.gk0wk-tiddlertoc-viewbuttonpopup-h4:hover,
.gk0wk-tiddlertoc-viewbuttonpopup-h5:hover,
.gk0wk-tiddlertoc-viewbuttonpopup-h6:hover {
  opacity: 1;
  background: <<colour background>>;
  color: <<colour foreground>>;
}
