/* styles for listing code */
ol.code-list {
	List-style-type: none;
	box-sizing:border-box;
}

ol.code-list {
	margin-top:0;
	margin-bottom:0;
}

ol.code-list  pre {
	padding:0 10px; 
	margin-top:0;
	margin-bottom:0;
	border:none;
	border-radius:0;
}


/* set the top and bottom padding for the first and last line of code */

ol.code-list li:first-child::before,
ol.code-list li:first-child pre{
	padding-top:3px;
}

ol.code-list li:last-child pre{
	padding-bottom:5px;
}

$:/plugins/kookma/hsl/styles/dark.css.text:
.code-list  pre {
	opacity:0.85; /* adjust the opacity of main code */
}

/* background color of emphasized lines*/
.code-list .code-selected pre{
	opacity:1;
	background-color:#121212; 
	/*other colors: #121212 black*/
}

/* the left border */
.code-list li{
	border-left:3px solid;
	border-left-color: yellow; /* change color as you like */
}

/* color of selected line number */
ol.code-list li.code-selected::before {
	color:orange;   /*#006699  orange*/
}

$:/plugins/kookma/hsl/styles/light.css.text:
/* adjust the opacity and */
.code-list  pre {
	opacity:0.5; 
}

/* background color of emphasized lines*/
.code-list .code-selected pre{
	opacity:1;
background-color:aliceblue; 
	/*other colors: LemonChiffon, bisque, aliceblue, BlanchedAlmond*/
}

/* the left border color: gutter */
.code-list li{
	border-left:3px solid;
	border-left-color:#6ce26c; /* change color as you like */
}

/* color of selected line number */
ol.code-list li.code-selected::before {
	color:#006699;   /*other colors  #4169E1  #006699*/
}

$:/plugins/kookma/hsl/styles/linenumbers.text:
/* style the line numbers */
ol.code-list {
	counter-reset: linenumber;
	position: relative;
}
ol.code-list li {
	counter-increment: linenumber;
}

/* customize the number */
ol.code-list li::before {
	/*	content: counter(linenumber) " ";*/
	left:5px;
	position: absolute;
	width: 26px;
	text-align: right;

	/* remove if not needed */
	color:#999;
	font-size:0.9em;
	font-family:monospace;
}

ol.code-list li.code-selected::before {
	content: counter(linenumber) " ";
}

