$:/plugins/kookma/utility/styles/centere.text:
\rules only filteredtranscludeinline transcludeinline macrodef macrocallinline html
<$list filter="[[$:/state/sidebar]get[text]match[no]]" variable=null>
:not([data-tiddler-title^="Draft of '"]).tc-tiddler-frame  {
min-width: 600px;
max-width: 50vw;
margin-left: auto;
margin-right: auto;
}
</$list>

$:/plugins/kookma/utility/styles/code.text:
figure.kk-utility-fig {
	/* for future use*/
	margin-left:0;
	margin-right:0;
}

/*figcaption structure */
figure.kk-utility-fig figcaption {
	padding: 4.5px 7.5px 7.5px 7.5px;;
	border-top-left-radius:  3px;
	border-top-right-radius: 3px;
	margin-bottom: -3px;
}

/* figcaption skin: colors */
figure.kk-utility-fig figcaption{
	opacity: 0.7;
	background-color:black;
	color:white;
}

/* correct the style of codeblock pre element  */
figure.kk-utility-fig pre{
	margin-top:0;
	/*	used to have inner top border straight */
	/*	border-top-left-radius:  0;  
	border-top-right-radius: 0; */
}


/* the code block and copy-to-clipboard button */
.kk-utility-code{
	position:  relative;
}
/* pretty display the empty code block */
.kk-utility-code pre code{
	min-height:30px; 
}

.kk-utility-copy-btn{
	position: absolute;
	top: 3px;
	right: 5px;
	opacity: 0.3;
	fill:currentColor;
}

.kk-utility-copy-btn:hover{
    transition: opacity 200ms ease-in-out;
    opacity: 1;
}

$:/plugins/kookma/utility/styles/doc-svg.text:
.kk-doc svg{
width: 1.2em;
height: 1.2em;
vertical-align: middle;}

.kk-ut-txt{
min-width:50%}

$:/plugins/kookma/utility/styles/main.text:
/* view feilds area */
.kk-ut-viewfields { /* prevents overlapping with other elements */
	margin-top: 12px;
	margin-bottom: 12px;
}
/* in simple transclusion for fields */
.kk-utility-tbl-borderless, 
.kk-utility-tbl-borderless tr,
.kk-utility-tbl-borderless td{border:0;}

$:/plugins/kookma/utility/styles/reveal-.text:
details.kk-utility-details summary {
	opacity:0.2;
	font-size:0.9em;
	display: inline;
	cursor: pointer;
	padding: 10px;
	transition: 0.3s;
	-webkit-user-select: none;
	-moz-user-select: none;
	-ms-user-select: none;
	user-select: none;
}
details.kk-utility-details summary:hover{

}
details.kk-utility-details[open] > summary {
	opacity:1;
}

$:/plugins/kookma/utility/styles/tw-vers.text:
/* TW-version added to site title */
.tc-site-title:before {
  content:"TW <<version>>";
  position:absolute;
  margin-top:-1.9em;
  color:silver;
  font-size:13px;
}
